    private void EnterPButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_EnterPButtonActionPerformed
        this.setVisible(false);
        ShowSpecserv s = new ShowSpecserv();
        s.setVisible(true);
    }//GEN-LAST:event_EnterPButtonActionPerformed

    private void EnterPButtonKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_EnterPButtonKeyPressed
        if(evt.getKeyCode() == KeyEvent.VK_ESCAPE){
            this.setVisible(false);
            new MainMenu().setVisible(true);
        }
        else if (evt.getKeyCode() == KeyEvent.VK_F11){
            this.setVisible(false);
        ShowSpecserv s = new ShowSpecserv();
        s.setVisible(true);
        }
        else if (evt.getKeyCode() == KeyEvent.VK_F12){
            this.setVisible(false);
            Show s = new Show();
            s.setVisible(true);
        }
        else if (evt.getKeyCode() == KeyEvent.VK_RIGHT){
            ModifyPButton.requestFocus();
        }
        else if (evt.getKeyCode() == KeyEvent.VK_ENTER){
            this.setVisible(false);
        ShowSpecserv s = new ShowSpecserv();
        s.setVisible(true);
        }
    }//GEN-LAST:event_EnterPButtonKeyPressed

