    private void BackRButtonKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_BackRButtonKeyPressed
        if(evt.getKeyCode() == KeyEvent.VK_ESCAPE){
            this.setVisible(false);
            new MainMenu().setVisible(true);
        }
        else if (evt.getKeyCode() == KeyEvent.VK_F11){
            this.setVisible(false);
        ShowSpecserv s = new ShowSpecserv();
        s.setVisible(true);
        }
        else if (evt.getKeyCode() == KeyEvent.VK_F12){
            this.setVisible(false);
            Show s = new Show();
            s.setVisible(true);
        }
        else if (evt.getKeyCode() == KeyEvent.VK_RIGHT){
            EnterPButton.requestFocus();
        }
        else if (evt.getKeyCode() == KeyEvent.VK_DOWN){
            EnterPButton.requestFocus();
        }
        else if (evt.getKeyCode() == KeyEvent.VK_ENTER){
            EnterPButton.requestFocus();
        }

    }//GEN-LAST:event_BackRButtonKeyPressed

